package springproject.restperson.controllers.base;import org.springframework.data.domain.Page;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestBody;import springproject.restperson.models.entities.Base;import java.io.Serializable;import java.util.List;public interface BaseController<T extends Base, I extends Serializable> {    ResponseEntity<List<T>> select();    ResponseEntity<Page<T>> pagination(@PathVariable Integer page);    ResponseEntity<T> get(@PathVariable I id);    ResponseEntity<T> insert(@RequestBody T t);    ResponseEntity<T> update(@PathVariable I id, @RequestBody T t);    ResponseEntity<T> delete(@PathVariable I id);}