package springproject.restperson.implementations;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import springproject.restperson.models.entities.Author;import springproject.restperson.repositories.AuthorRepository;import springproject.restperson.services.AuthorService;@Servicepublic class AuthorServiceImpl extends BaseServiceImpl<Author, Long> implements AuthorService {    @Autowired    private AuthorRepository authorRepository;    @Override    public Author update(Long id, Author author) throws Exception {        try {            Author dbAuthor = authorRepository.findByIdAndStatus(id, "ENABLED");            dbAuthor.setName(author.getName());            dbAuthor.setLastName(author.getLastName());            dbAuthor.setBiography(author.getBiography());            return authorRepository.save(dbAuthor);        } catch (Exception e) {            throw new Exception(e.getMessage());        }    }}