package springproject.restperson.implementations;import lombok.extern.java.Log;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import springproject.restperson.models.entities.Location;import springproject.restperson.repositories.LocationRepository;import springproject.restperson.services.LocationService;@Log@Servicepublic class LocationServiceImpl extends BaseServiceImpl<Location, Long> implements LocationService {    @Autowired    private LocationRepository locationRepository;    @Override    public Location update(Long id, Location location)  {        try {            Location dbLocation = locationRepository.findByIdAndStatus(id, "ENABLED");            dbLocation.setDenomination(location.getDenomination());            return locationRepository.save(dbLocation);        } catch (Exception e) {            log.warning(e.getMessage());        }        return null;    }}