package springproject.restperson.implementations;import lombok.extern.java.Log;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import springproject.restperson.models.entities.Person;import springproject.restperson.repositories.PersonRepository;import springproject.restperson.services.PersonService;@Log@Servicepublic class PersonServiceImpl extends BaseServiceImpl<Person, Long> implements PersonService {    @Autowired    private PersonRepository personRepository;    @Override    public Person update(Long id, Person person) {        try {            Person dbPerson = personRepository.findByIdAndStatus(id, "ENABLED");            dbPerson.setName(person.getName());            dbPerson.setLastName(person.getLastName());            dbPerson.setDocument(person.getDocument());            dbPerson.setAge(person.getAge());            return personRepository.save(dbPerson);        } catch (Exception e) {            log.warning(e.getMessage());        }        return null;    }}