package springproject.restperson.models.entities;import lombok.*;import org.hibernate.envers.Audited;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Table;@Entity@Table(name = "authors")@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Auditedpublic class Author extends Base {    @Column(name = "name")    private String name;    @Column(name = "last_name")    private String lastName;    @Column(name = "biography", length = 1500)    private String biography;}