package springproject.restperson.models.entities;import lombok.*;import javax.persistence.*;import java.io.Serializable;@MappedSuperclass@Getter@Setter@AllArgsConstructor@NoArgsConstructorpublic class Base implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "status")    private String status;}