package springproject.restperson.models.entities;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import org.hibernate.envers.Audited;import javax.persistence.*;@Entity@Table(name = "homes")@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Auditedpublic class Home extends Base {    @Column(name = "street")    private String street;    @Column(name = "number")    private String number;    @ManyToOne(optional = false)    @JoinColumn(name = "id_location")    private Location location;}