package springproject.restperson.models.entities;import lombok.*;import org.hibernate.envers.Audited;import javax.persistence.*;@Entity@Table(name = "people")@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Auditedpublic class Person extends Base {    @Column(name = "name")    private String name;    @Column(name = "last_name")    private String lastName;    @Column(name = "document")    private String document;    @Column(name = "age")    private int age;}