package springproject.restperson.models.entities;import lombok.*;import org.hibernate.envers.Audited;import javax.persistence.*;import java.util.List;@Entity@Table(name = "people")@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Auditedpublic class Person extends Base {    @Column(name = "name")    private String name;    @Column(name = "last_name")    private String lastName;    @Column(name = "document")    private String document;    @Column(name = "age")    private int age;    @OneToOne(cascade = CascadeType.ALL)    @JoinColumn(name = "id_home")    private Home home;    @OneToMany(cascade = CascadeType.ALL, orphanRemoval = true)    @JoinTable(name = "person_book",            joinColumns = @JoinColumn(name = "person_id"),            inverseJoinColumns = @JoinColumn(name = "book_id"))    private List<Book> books;}