package springproject.restperson.repositories;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.repository.NoRepositoryBean;import springproject.restperson.models.entities.Base;import java.io.Serializable;import java.util.List;@NoRepositoryBeanpublic interface BaseRepository<T extends Base, I extends Serializable> extends JpaRepository<T, I> {    List<T> findByStatus(String status);    Page<T> findByStatus(Pageable pageable, String status);    T findByIdAndStatus(I id, String status);}