package springproject.restperson.repositories;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.repository.NoRepositoryBean;import springproject.restperson.models.entities.Base;import java.io.Serializable;import java.util.List;@NoRepositoryBeanpublic interface BaseRepository<E extends Base, ID extends Serializable> extends JpaRepository<E, ID> {    List<E> findByStatus(String status);    E findByIdAndStatus(ID id, String status);}