package springproject.restperson.services;import springproject.restperson.models.entities.Base;import java.io.Serializable;import java.util.List;public interface BaseService<E extends Base, ID extends Serializable> {    List<E> select(String status) throws Exception;    E get(ID id,String status) throws Exception;    E insert(E entity) throws Exception;    E update(ID id, E entity) throws Exception;    E delete(ID id) throws Exception;}