package springproject.restperson.services;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import springproject.restperson.models.entities.Base;import java.io.Serializable;import java.util.List;public interface BaseService<T extends Base, I extends Serializable> {    List<T> select(String status);    Page<T> select(Pageable pageable, String status);    T get(I id, String status);    T insert(T t);    T update(I id, T t);    T delete(I id);}